declare module 'mext-plugin' {
  import MextNodePlugin from 'mext-plugin/lib/node/plugin';
  import MextClientPlugin from 'mext-plugin/lib/client/plugin';
  export * from 'mext-plugin/types/client';
  export * from 'mext-plugin/types/node';

  /**
   * Mext插件核心API
   ```
   export default class extends Plugin.Node{}

   export default class extends Plugin.Client{}
   ```
   */
  const Plugin = {
    /**
     * Node环境运行的插件类
     */
    Node: MextNodePlugin,

    /**
     * 客户端环境（浏览器）运行的插件类
     */
    Client: MextClientPlugin
  };

  export default Plugin;
}
