interface Iemitter {
    /**
     * 创建监听
     *
     * @param event 监听名称
     * @param cb 监听触发回调
     */
    on(event: string, cb: (...data: any[]) => void): void;
    /**
     * 触发监听
     * @param event 监听名称
     * @param data 传输值
     */
    emit(event: string, ...data: any[]): void;
    /** 移除监听 */
    off(event: string): void;
}
declare const _default: {
    storeEmitter: (emit: any) => void;
    getEmitter: () => Iemitter;
};
export default _default;
