/**
 * 初始化基础变量
 */
import * as React from 'react';
import { History } from 'history';
import { ILifeCycletoFrom } from './type';
export default class Base extends React.Component {
    /** 基础数据 */
    routes: any;
    match_routes: any;
    lastMatchRoutes: any;
    lastRoute: any;
    lastTarget: ILifeCycletoFrom;
    target: ILifeCycletoFrom;
    mext_initialState: any;
    emitter: any;
    /** 数据交互更新 */
    updateInitialState: Function;
    getInitialState: Function;
    updateProps: Function | boolean;
    getInitialProps: Function;
    /**  其他 */
    cleanError: Function;
    forceRenderRouter: Function;
    history: History;
    PromptContext: any;
    scrollParam: any;
    pathname: any;
    leavePaths: never[];
    routeIsSwitch: boolean;
    routeChanged: boolean;
    lastQuery: {};
    exportPath: null;
    constructor(...args: any[]);
}
